INSERT INTO `document` (`id_document`, `file_name`, `file_link`, `description`, `num_download`) VALUES
(1, 'Global Issues and Challenges', 'Global Issues and Challenges.pdf', 'Modul Global Issues and Challenges bertujuan untuk meningkatkan pemahaman peserta mengenai isu dan tantangan global. Berbagai aktivitas manusia dan alam memberikan dampak   kesehatan manusia, hewan, dan ekosistem baik secara langsung maupun tidak langsung. Setelah menyelesaikan modul ini peserta diharapkan memiliki pemahaman mengenai dinamika ekosistem dan menganalisis bagaimana perubahan alam dapat memberikan dampak terhadap kesehatan manusia, hewan, dan lingkungan secara lokal, regional, dan nasional. ', 1),
(2, 'One Health Concept and Knowledge', 'One Health Concept and Knowledge.pdf', 'Modul One Health Concept and Knowledge bertujuan untuk memberikan pemahaman peserta mengenai pendekatan One Health, mencakup sejarah, anggota tim, serta aplikasi. Ancaman kesehatan yang dihadapi dewasa ini membutuhkan pendekatan One Health yang mempertemukan ahli dan stakeholder yang beragam. Setelah menyelesaikan modul ini peserta diharapkan memiliki pemahaman mengenai One Health dan aplikasinya.a ', 2),
(3, 'Behavior Development', 'Behavior Development and Communication.pdf', 'Modul Behavior Development bertujuan untuk meningkatkan pemahaman peserta mengenai model dan konsep perubahan perilaku. Setelah menyelesaikan modul ini peserta diharapkan mampu mengevaluasi model perubahan perilaku ke dalam konteks One Health dan mengadaptasinya untuk intervensi One Health. Melalui modul ini peserta juga dapat memahami bahwa perubahan perilaku juga memberikan dampak terhadap kesehatan manusia, hewan, dan lingkungan.', 2),
(4, 'Collaboration and Partnership', 'Collaboration and Partnership.pdf', 'Modul Collaboration and Partnership bertujuan untuk memberikan pemahaman peserta mengenai prinsip dasar kolaborasi dan kemitraan menggunakan pendekatan One Health dalam menghadapi tantangan kesehatan. Kolaborasi lintas sektor dan disiplin ilmu dibutuhkan dalam meningkatkan kesiapan menghadapi situasi darurat khususnya dalam menghadapi wabah. Setelah menyelesaikan modul ini peserta diharapkan dapat memahami pentingnya kolaborasi dan peran dari setiap sektor yang terlibat dalam perancangan rencana kerja sama.', 1),
(5, 'Culture, Beliefs, Values, and Ethics', 'Culture, Beliefs, Values, and Ethics.pdf', 'Modul Culture, Beliefs, Values, and Ethics bertujuan untuk meningkatkan pemahaman peserta mengenai kebudayaan, kepercayaan, nilai, dan etika yang berbeda dalam setiap kelompok masyarakat. Pemahaman tersebut dapat membantu peserta dalam menjalin kerja sama lintas sektor dan disiplin ilmu, serta dalam berinteraksi dengan masyarakat. Setelah menyelesaikan modul ini peserta diharapkan memiliki kemampuan mengidentifikasi dan menganalisis budaya dan kearifan lokal, serta masalah-masalah yang berkaitan. ', 0),
(6, 'Epidemiology and Risk Analysis', 'Epidemiology and Risk Analysis.pdf', 'Modul Epidemiology and Risk Analysis bertujuan untuk meningkatkan pemahaman peserta mengenai kejadian penyakit dan pentingnya proses penilaian risiko. Peserta akan mempelajari mengenai komponen dan kegunaan epidemiologi, serta transmisi penyakit. Setelah menyelesaikan modul ini peserta diharapkan mampu mengidentifikasi peran epidemiologi dan analisis risiko pada setting One Health', 1),
(7, 'Fundamental of Infectious Disease', 'Fundamental of Infectious Disease.pdf', 'Modul Fundamental of Infectious Disease bertujuan untuk meningkatkan pemahaman peserta mengenai prinsip dasar penyakit infeksi dalam konteks One Health. Modul ini akan menggunakan pendekatan One Health dalam membahas penyakit infeksi dan kaitannya dengan kehidupan sehari-hari. Setelah menyelesaikan modul ini peserta diharapkan mampu mendeskripsikan prinsip-prinsip penyakit infeksi.', 0),
(8, 'Infectious Disease Management', 'Infectious Disease Management.pdf', 'Modul Infectious Disease Management bertujuan untuk meningkatkan pemahaman peserta mengenai konsep manajemen penyakit, baik pada tingkat infividu maupun populasi. Manajemen penyakit infeksi merupakan pembelajaran yang komprehensif dalam menganalisis suatu masalah kesehatan. Setelah menyelesaikan modul ini peserta diharapkan mampu mengidentifikasi dan menganalisis faktor risiko suatu wabah dan merancang rencana aksi manajemen penyakit.', 0),
(9, 'Life Values of A True Leader', 'Life Values of A True Leaders.pdf', 'Modul Life Values of True Leaders bertujuan untuk meningkatkan pemahaman peserta mengenai nilai dan etika seorang pemimpin serta memahami pentingnya hal tersebut dalam mencapai tujuan One Health. Setelah menyelesaikan modul ini peserta diharapkan memiliki kesadaran terhadap nilai, etika, dan profesionalisme dalam merencanakan dan mengimplementasikan intervensi One Health.', 0),
(10, 'Policy, Advocacy, and Regulation', 'Policy, Advocacy, and Regulation.pdf', 'Modul Policy, Advocacy, and Regulation bertujuan untuk meningkatkan pemahaman peserta mengenai proses advokasi dan bagaimana advokasi dapat mempengaruhi inisiasi One Health. Dalam proses pembelajaran, peserta akan mengidentifkasi peran dan kebijakan yang dikeluarkan oleh institusi nasional maupun internasional. Setelah menyelesaikan modul ini peserta diharapkan mampu merancang rencana advokasi terkait inisiasi One Health.', 0),
(11, 'Public Leaders', 'Public Leaders.pdf', 'Modul Public Leaders bertujuan untuk memfasilitasi peserta dalam mendiskusikan kepemimpinan dalam konteks One Health yang mampu menginisiasi aksi untuk perubahan yang positif. Melalui modul ini, peserta akan mengeksplor mengenai kapasitas dan kemampuan yang harus dimiliki oleh seorang pemimpin serta menganalisis gaya kepemimpinan masing-masing. Setelah menyelesaikan modul ini peserta diharapkan mampu mengaplikasikan kapasitas dan kemampuan tersebut dalam melibatkan dan memberdayakan masyarakat. ', 0),
(12, 'Social Communication', 'Social Communication.pdf', 'Modul Social Communication bertujuan untuk memberikan pemahaman kepada peserta mengenai prinsip komunikasi yang efektif. Sebagai tenaga kesehatan One Health, peserta harus mampu untuk berkomunikasi baik kepada masyarakat, kolega, maupun pihak yang terlibat dalam bekerja dan berkolaborasi. Setelah menyelesaikan modul ini, peserta diharapkan memahami prinsip berkomunikasi dan mampu berkomunikasi secara efektif.', 0);

