  `shortbio` varchar(500) NOT NULL,
  `bio` varchar(3500) NOT NULL,
  `address` varchar(100) NOT NULL,
  `phone` varchar(100) NOT NULL,
  `email` varchar(100) NOT NULL,
  `speaker_photo` varchar(100) NOT NULL,
  `info_program` varchar(100) NOT NULL,
  `before_name` varchar(100) NOT NULL,
  `after_name` varchar(100) NOT NULL,
  `institution` varchar(100) NOT NULL,
  `type_institution` varchar(50) NOT NULL,
  `pos_institution` varchar(100) NOT NULL,
  `occupation` varchar(50) NOT NULL,
  `area_of_work` varchar(100) NOT NULL,
  `disciplinary` varchar(50) NOT NULL,
  `resume` varchar(3500) NOT NULL,
  `expertise` varchar(3500) NOT NULL,
  `country` varchar(50) NOT NULL,
  `province` varchar(50) NOT NULL,
  `cv` varchar(50) NOT NULL,
  PRIMARY KEY (`id_speaker`)
) ENGINE=InnoDB  DEFAULT CHARSET=latin1 AUTO_INCREMENT=11 ;

